mapper.select:
select * from recharge_record where open_id=#{openId}
 and

  create_time >= #{startTime}

 and

  create_time <= #{endTime}

order by create_time desc

select * from recharge_record where 1=1
and

  create_time >= #{startTime}

 and

  create_time <= #{endTime}

  order by create_time desc
